import { MongoClient } from "mongodb";

const mongoURI = process.env.MONGO_URI; // ✅ Use Railway MongoDB

export async function fetchCandidatesFromMongo() {
    const client = new MongoClient(MONGO_URI);
    try {
        await client.connect();
        const db = client.db("your-database-name"); // ✅ Use your MongoDB database name
        const candidates = await db.collection("candidates").find().toArray();
        console.log("MongoDB Candidates (Direct Fetch):", candidates);
        return candidates;
    } catch (error) {
        console.error("Error fetching candidates:", error);
        return [];
    } finally {
        await client.close();
    }
}
